mapper.update:
update board
set title=#{title}
where board_no=#{boardNo}

mapper.select:
select board_no boardNo, title
from board
where board_no=#{boardNo}

select file_no fileNo, filename , origin_name originName, file_size fileSize, file_extension fileExtension
from boardfile
where board_no=#{boardNo}

mapper.insert:
insert into boardfile(
	board_no, filename, origin_name, file_size, file_type, file_extension
) values (
	#{boardNo}, #{filename}, #{originName}, #{fileSize}, #{fileType}, #{fileExtension}
)
